import imageLib from './'
import fs from 'fs'

console.log(imageLib)

const png = fs.readFileSync('./test.png')

const arr = imageLib.PNGToRGBAArray(png)

const generatedPng = imageLib.RGBAArrayToPNG(arr.rgba, arr.png)

fs.writeFileSync('./test-generated.png', generatedPng)

console.log(png, generatedPng, generatedPng.toString() == png.toString())

